INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (78, 'Jules', '/yMOWu8rpZPiBI0j8MInBTtNO0vU.jpg', 'A flying saucer lands in the backyard of an elderly suburbanite with memory problems, who forms a bond with the scared alien inside.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (79, 'Star Wars', '/6FfCtAuVAW8XJjZ7eWeLibRLWTw.jpg', 'Princess Leia is captured and held hostage by the evil Imperial forces in their effort to take over the galactic Empire. Venturesome Luke Skywalker and dashing captain Han Solo team together with the loveable robot duo R2-D2 and C-3PO to rescue the beautiful princess and restore peace and justice in the Empire.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (80, 'Insidious: The Red Door', '/d07phJqCx6z5wILDYqkyraorDPi.jpg', 'To put their demons to rest once and for all, Josh Lambert and a college-aged Dalton Lambert must go deeper into The Further than ever before, facing their family''s dark past and a host of new and more horrifying terrors that lurk behind the red door.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (81, 'Asteroid City', '/tcKBclNUdkas4Jis8RYYZnPdTIm.jpg', 'In an American desert town circa 1955, the itinerary of a Junior Stargazer/Space Cadet convention is spectacularly disrupted by world-changing events.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (82, 'Dungeons & Dragons: Honor Among Thieves', '/A7AoNT06aRAc4SV89Dwxj3EYAgC.jpg', 'A charming thief and a band of unlikely adventurers undertake an epic heist to retrieve a lost relic, but things go dangerously awry when they run afoul of the wrong people.');
