INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (84, 'Chang An', '/xe7TfaryOTdmn8BEU8fNlYfVQhS.jpg', 'A few years after the Anshi Rebellion, the Tibetan army invaded the southwest and the Tang dynasty military commander Gao Shi suffered a defeat in battle. Chang''an was in imminent danger. Gao Shi, who was trapped in the besieged city, recalled his past experiences with Li Bai to the eunuch who served as the military supervisor.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (85, 'Spider-Man: No Way Home', '/5weKu49pzJCt06OPpjvT80efnQj.jpg', 'Peter Parker is unmasked and no longer able to separate his normal life from the high-stakes of being a super-hero. When he asks for help from Doctor Strange the stakes become even more dangerous, forcing him to discover what it truly means to be Spider-Man.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (86, 'King of Killers', '/x5AreOAgkTBzUSL58o4jsYortw2.jpg', 'Garan is a part of a group of international hitmen who are contracted to take out the most dangerous killer in the world, only to find out that they''re the ones being hunted.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (87, 'Avatar', '/kyeqWdyUXW608qlYkRqosgbbJyK.jpg', 'In the 22nd century, a paraplegic Marine is dispatched to the moon Pandora on a unique mission, but becomes torn between following orders and protecting an alien civilization.');
