INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (90, 'The Dark Knight', '/qJ2tW6WMUDux911r6m7haRef0WH.jpg', 'Batman raises the stakes in his war on crime. With the help of Lt. Jim Gordon and District Attorney Harvey Dent, Batman sets out to dismantle the remaining criminal organizations that plague the streets. The partnership proves to be effective, but they soon find themselves prey to a reign of chaos unleashed by a rising criminal mastermind known to the terrified citizens of Gotham as the Joker.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (91, 'The Black Book', '/kn28W24slBLyGr8ZIZnxNE5YZrY.jpg', 'After his son is wrongly accused of kidnapping, a deacon who has just lost his wife takes matters into his own hands and fights a crooked police gang to clear him.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (92, 'Monster High 2', '/mClrCMfGpOqTSiuCTSK67WZ92x5.jpg', 'As they enter sophomore year at Monster High, Clawdeen Wolf, Draculaura and Frankie Stein face new students, new powers, and an even bigger threat that could not only tear their friendship apart but could change the world forever.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (93, 'Avengers: Endgame', '/or06FN3Dka5tukK1e9sl16pB3iy.jpg', 'After the devastating events of Avengers: Infinity War, the universe is in ruins due to the efforts of the Mad Titan, Thanos. With the help of remaining allies, the Avengers must assemble once more in order to undo Thanos'' actions and restore order to the universe once and for all, no matter what consequences may be in store.');
INSERT INTO public."Movies" ("Id", "Title", "PosterUrl", "Description") VALUES (94, 'The Lord of the Rings: The Fellowship of the Ring', '/6oom5QYQ2yQTMJIbnvbkBL9cHo6.jpg', 'Young hobbit Frodo Baggins, after inheriting a mysterious ring from his uncle Bilbo, must leave his home in order to keep it from falling into the hands of its evil creator. Along the way, a fellowship is formed to protect the ringbearer and make sure that the ring arrives at its final destination: Mt. Doom, the only place where it can be destroyed.');
